import { Component, ElementRef, OnInit, ViewChild } from '@angular/core';

import { CUSTOM_ELEMENTS_SCHEMA } from '@angular/core';
import { IonicModule } from '@ionic/angular';
import { Router } from '@angular/router';
import { Storage } from '@ionic/storage-angular';

@Component({
  selector: 'page-welcome',
  templateUrl: 'welcome.html',
  styleUrls: ['./welcome.scss'],
  standalone: true,
  imports: [IonicModule],
  schemas: [CUSTOM_ELEMENTS_SCHEMA]
})
export class WelcomePage implements OnInit {
  showSkip = true;
  @ViewChild('slides') slides: ElementRef;

  constructor(
    private router: Router,
    private storage: Storage
  ) { }

  async ngOnInit() {
    await this.storage.create();
    // Check if user has already seen the tutorial
    const seen = await this.storage.get('hasSeenTutorial');
    if (seen === 'true') {
      this.router.navigateByUrl('/login', { replaceUrl: true });
    }
  }

  async startApp() {
    await this.storage.set('hasSeenTutorial', 'true');
    this.router.navigateByUrl('/login', { replaceUrl: true });
  }

  async onSlideChange() {
    if (this.slides && this.slides.nativeElement) {
      const swiper = this.slides.nativeElement.swiper;
      if (swiper) {
        this.showSkip = !swiper.isEnd;
      }
    }
  }
}
<ion-header>
  <ion-toolbar>
    <ion-buttons slot="end">
      <ion-button color="primary" (click)="startApp()" [hidden]="!showSkip">Skip</ion-button>
    </ion-buttons>
  </ion-toolbar>
</ion-header>

<ion-content>
  <swiper-container #slides pagination="true" (slidechange)="onSlideChange()">
    <swiper-slide>
      <div class="slide-container">
        <img src="assets/img/ica-slidebox-img-1.png" class="slide-image" />
        <h2 class="slide-title">
          Welcome to <b>SAFE SPACE</b>
        </h2>
        <p>
          A <b>Gender-based violence (GBV)</b> platform to raise awareness on GBV.
        </p>
      </div>
    </swiper-slide>

    <swiper-slide>
      <div class="slide-container">
        <img src="assets/img/ica-slidebox-img-2.png" class="slide-image" />
        <h2 class="slide-title">What is GBV?</h2>
        <p>
          <b>Gender-based violence</b> is violence directed against a person because of their gender.
        </p>
      </div>
    </swiper-slide>

    <swiper-slide>
      <div class="slide-container">
        <img src="assets/img/ica-slidebox-img-3.png" class="slide-image" />
        <h2 class="slide-title">How can we help?</h2>
        <p>
          We provide resources, support, and a safe space to discuss and address GBV issues.
        </p>
      </div>
    </swiper-slide>

    <swiper-slide>
      <div class="slide-container">
        <img src="assets/img/ica-slidebox-img-4.png" class="slide-image" />
        <h2 class="slide-title">Ready to Start?</h2>
        <ion-button fill="clear" (click)="startApp()">
          Continue
          <ion-icon slot="end" name="arrow-forward"></ion-icon>
        </ion-button>
      </div>
    </swiper-slide>
  </swiper-container>
</ion-content>
