import { Component, OnInit } from '@angular/core';
import { FormGroup, FormBuilder, Validators, FormControl } from '@angular/forms';
import { NavController, ModalController } from '@ionic/angular';
import { AuthenticationService } from '../../services/authentication.service';
import { Database, get, getDatabase, onValue, push, ref, set } from '@angular/fire/database';
import { Auth, authState, user } from '@angular/fire/auth';

@Component({
  selector: 'app-user-reports',
  templateUrl: './user-reports.page.html',
  styleUrls: ['./user-reports.page.scss'],
})

export class UserReportsPage implements OnInit {
  validations_form: FormGroup;
  errorMessage: string = '';
  successMessage: string = '';
  reports = [];
  showForm = false;

  constructor(
    private navCtrl: NavController,
    private formBuilder: FormBuilder,
    private database: Database,
    private auth: Auth,
    private modalController: ModalController
  ) {}

  ngOnInit() {
    this.validations_form = this.formBuilder.group({
      name: new FormControl('', Validators.required),
      number: new FormControl('', Validators.required),
      message: new FormControl('', Validators.required)
    });
    this.loadReports();
  }

  openReportForm() {
    this.showForm = true;
  }

  async loadReports() {
    if (this.auth.currentUser) {
      const userReportsRef = ref(this.database, `reports/${this.auth.currentUser.uid}`);
      onValue(userReportsRef, (snapshot) => {
        this.reports = [];
        snapshot.forEach((childSnapshot) => {
          const report = childSnapshot.val();
          report.id = childSnapshot.key;
          this.reports.push(report);
        });
      });
    }
  }

  async sendReport(value) {
    if (this.auth.currentUser) {
      const userReportsRef = ref(this.database, `reports/${this.auth.currentUser.uid}`);
      const newReportRef = push(userReportsRef);
      await set(newReportRef, value);
      this.validations_form.reset();
      this.showForm = false;
      this.navCtrl.navigateForward('/home/user-reports');
    }
  }

  async getReport(id: string) {
    if (this.auth.currentUser) {
      const reportRef = ref(this.database, `reports/${this.auth.currentUser.uid}/${id}`);
      const snapshot = await get(reportRef);
      return snapshot.val();
    }
    return null;
  }
}
<ion-header>
  <ion-toolbar>
    <ion-buttons slot="start">
      <ion-menu-button></ion-menu-button>
    </ion-buttons>
    <ion-title>Reports</ion-title>
  </ion-toolbar>
</ion-header>

<ion-content fullscreen="true">
  <ion-header collapse="condense">
    <ion-toolbar>
      <ion-title size="large">Reports</ion-title>
    </ion-toolbar>
  </ion-header>

  <ion-grid fixed *ngIf="!showForm">
    <ion-row *ngIf="reports.length === 0">
      <ion-col size="12" class="ion-text-center">
        <ion-card>
          <ion-card-header>
            <ion-card-title>No Reports Yet</ion-card-title>
          </ion-card-header>
          <ion-card-content>
            <p>You haven't submitted any reports yet.</p>
          </ion-card-content>
        </ion-card>
      </ion-col>
    </ion-row>

    <ion-row>
      <ion-col size="12" size-md="6" *ngFor="let report of reports">
        <ion-card>
          <ion-card-header>
            <ion-card-title>{{report.name}}</ion-card-title>
            <ion-card-subtitle>{{report.number}}</ion-card-subtitle>
          </ion-card-header>

          <ion-card-content>
            <p>{{report.message}}</p>
          </ion-card-content>
        </ion-card>
      </ion-col>
    </ion-row>
  </ion-grid>

  <ion-fab vertical="bottom" horizontal="end" slot="fixed" *ngIf="!showForm">
    <ion-fab-button (click)="openReportForm()">
      <ion-icon name="add"></ion-icon>
    </ion-fab-button>
  </ion-fab>

  <!-- Report Form -->
  <div *ngIf="showForm" class="ion-padding">
    <h2>Submit New Report</h2>
    <form [formGroup]="validations_form" (ngSubmit)="sendReport(validations_form.value)">
      <ion-item>
        <ion-label position="floating">Name</ion-label>
        <ion-input formControlName="name" type="text"></ion-input>
      </ion-item>

      <ion-item>
        <ion-label position="floating">Phone Number</ion-label>
        <ion-input formControlName="number" type="tel"></ion-input>
      </ion-item>

      <ion-item>
        <ion-label position="floating">Message</ion-label>
        <ion-textarea formControlName="message" rows="6"></ion-textarea>
      </ion-item>

      <div class="ion-padding">
        <ion-button expand="block" type="submit" [disabled]="!validations_form.valid">Submit Report</ion-button>
        <ion-button expand="block" fill="outline" (click)="showForm = false">Cancel</ion-button>
      </div>
    </form>
  </div>
</ion-content>
