import { Component, OnInit, ViewEncapsulation, inject } from '@angular/core';
import { MenuController, Platform, ToastController } from '@ionic/angular';
import { Router } from '@angular/router';
import { Storage } from '@ionic/storage-angular';
import { SwUpdate } from '@angular/service-worker';
import { UserData } from './providers/user-data';
import { environment } from '../environments/environment';
import { IonicModule } from '@ionic/angular';
import { RouterModule } from '@angular/router';
import { FormsModule } from '@angular/forms';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
  encapsulation: ViewEncapsulation.None,
  standalone: true,
  imports: [
    IonicModule,
    RouterModule,
    FormsModule
  ]
})
export class AppComponent implements OnInit {
  private menu = inject(MenuController);
  private platform = inject(Platform);
  private router = inject(Router);
  private storage = inject(Storage);
  private userData = inject(UserData);
  private swUpdate = inject(SwUpdate);
  private toastCtrl = inject(ToastController);

  appPages = [
    {
      title: 'News Feed',
      url: '/app/tabs/news-feed',
      icon: 'list'
    },
    {
      title: 'Social Workers',
      url: '/app/tabs/social-workers',
      icon: 'people'
    },
    {
      title: 'Chat Rooms',
      url: '/app/tabs/chatrooms',
      icon: 'chatbubbles'
    },
    {
      title: 'Report',
      url: '/app/tabs/reports',
      icon: 'megaphone'
    },
    {
      title: 'Help Centers',
      url: '/app/tabs/map',
      icon: 'map'
    },
    {
      title: 'About',
      url: '/app/tabs/about',
      icon: 'information-circle'
    }
  ];

  loggedIn = false;
  dark = false;
  appName = environment.appName;
  version = environment.version;

  constructor() {
    this.initializeApp();
  }

  async ngOnInit() {
    await this.storage.create();
    this.checkLoginStatus();
    this.listenForLoginEvents();
    this.setupServiceWorker();
  }

  private async setupServiceWorker() {
    if (this.swUpdate.isEnabled) {
      this.swUpdate.versionUpdates.subscribe(event => {
        if (event.type === 'VERSION_READY') {
          this.promptUserUpdate();
        }
      });
    }
  }

  private async promptUserUpdate() {
    const toast = await this.toastCtrl.create({
      message: 'Update available!',
      position: 'bottom',
      buttons: [
        {
          role: 'cancel',
          text: 'Reload'
        }
      ]
    });

    await toast.present();

    toast.onDidDismiss()
      .then(() => this.swUpdate.activateUpdate())
      .then(() => window.location.reload());
  }

  private initializeApp() {
    this.platform.ready().then(() => {
      if (this.platform.is('hybrid')) {
        this.setupStatusBar();
      }
    });
  }

  private setupStatusBar() {
    const statusBar = document.querySelector('ion-status-bar') as HTMLElement;
    if (statusBar) {
      statusBar.style.backgroundColor = this.dark ? '#000000' : '#ffffff';
    }
  }

  async checkLoginStatus() {
    const loggedIn = await this.userData.isLoggedIn();
    this.updateLoggedInStatus(loggedIn);
  }

  private updateLoggedInStatus(loggedIn: boolean) {
    this.loggedIn = loggedIn;
  }

  private listenForLoginEvents() {
    window.addEventListener('user:login', () => this.updateLoggedInStatus(true));
    window.addEventListener('user:signup', () => this.updateLoggedInStatus(true));
    window.addEventListener('user:logout', () => this.updateLoggedInStatus(false));
  }

  async logout() {
    await this.userData.logout();
    await this.router.navigateByUrl('/app/tabs/schedule');
  }

  async openTutorial() {
    this.menu.enable(false);
    await this.storage.set('ion_did_tutorial', false);
    await this.router.navigateByUrl('/tutorial');
  }
}
<ion-app [class.dark-theme]="dark">
  <ion-split-pane contentId="main-content">
    <ion-menu contentId="main-content" type="overlay">
      <ion-content>
        <ion-list lines="none">
          <ion-list-header>
            <ion-label class="ion-text-center">
              <ion-text color="dark">{{ appName }}</ion-text>
              <ion-text color="primary"> v{{ version }}</ion-text>
            </ion-label>
          </ion-list-header>

          <ion-menu-toggle autoHide="false" *ngFor="let p of appPages">
            <ion-item [routerLink]="p.url" routerLinkActive="selected" routerDirection="root" detail="false">
              <ion-icon slot="start" [name]="p.icon" color="primary"></ion-icon>
              <ion-label>{{ p.title }}</ion-label>
            </ion-item>
          </ion-menu-toggle>
        </ion-list>

        <ion-list *ngIf="loggedIn" lines="none">
          <ion-list-header>
            <ion-label>Account</ion-label>
          </ion-list-header>

          <ion-menu-toggle autoHide="false">
            <ion-item routerLink="/account" routerLinkActive="active" routerDirection="root" detail="false">
              <ion-icon slot="start" name="person" color="primary"></ion-icon>
              <ion-label>Account</ion-label>
            </ion-item>
          </ion-menu-toggle>

          <ion-menu-toggle autoHide="false">
            <ion-item routerLink="/support" routerLinkActive="active" routerDirection="root" detail="false">
              <ion-icon slot="start" name="help-circle" color="primary"></ion-icon>
              <ion-label>Support</ion-label>
            </ion-item>
          </ion-menu-toggle>

          <ion-menu-toggle autoHide="false">
            <ion-item button (click)="logout()" detail="false">
              <ion-icon slot="start" name="log-out" color="primary"></ion-icon>
              <ion-label>Logout</ion-label>
            </ion-item>
          </ion-menu-toggle>
        </ion-list>

        <ion-list *ngIf="!loggedIn" lines="none">
          <ion-list-header>
            <ion-label>Account</ion-label>
          </ion-list-header>

          <ion-menu-toggle autoHide="false">
            <ion-item routerLink="/login" routerLinkActive="active" routerDirection="root" detail="false">
              <ion-icon slot="start" name="log-in" color="primary"></ion-icon>
              <ion-label>Login</ion-label>
            </ion-item>
          </ion-menu-toggle>

          <ion-menu-toggle autoHide="false">
            <ion-item routerLink="/support" routerLinkActive="active" routerDirection="root" detail="false">
              <ion-icon slot="start" name="help-circle" color="primary"></ion-icon>
              <ion-label>Support</ion-label>
            </ion-item>
          </ion-menu-toggle>

          <ion-menu-toggle autoHide="false">
            <ion-item routerLink="/signup" routerLinkActive="active" routerDirection="root" detail="false">
              <ion-icon slot="start" name="person-add" color="primary"></ion-icon>
              <ion-label>Signup</ion-label>
            </ion-item>
          </ion-menu-toggle>

          <ion-item>
            <ion-icon slot="start" name="moon" color="primary"></ion-icon>
            <ion-label>Dark Mode</ion-label>
            <ion-toggle [(ngModel)]="dark" color="primary"></ion-toggle>
          </ion-item>
        </ion-list>

        <ion-list lines="none">
          <ion-list-header>
            <ion-label>Welcome</ion-label>
          </ion-list-header>
          <ion-menu-toggle autoHide="false">
            <ion-item button (click)="openTutorial()" detail="false">
              <ion-icon slot="start" name="home" color="primary"></ion-icon>
              <ion-label>Welcome Slides</ion-label>
            </ion-item>
          </ion-menu-toggle>
        </ion-list>
