import { Component, ViewChild } from '@angular/core';
import { IonicSlides, MenuController } from '@ionic/angular';

import { Router } from '@angular/router';
import { Storage } from '@ionic/storage-angular';

@Component({
  selector: 'page-welcome',
  templateUrl: 'welcome.html',
  styleUrls: ['./welcome.scss'],
})
export class WelcomePage {
  showSkip = true;

  @ViewChild('slides') slides: any;

  constructor(
    public menu: MenuController,
    public router: Router,
    public storage: Storage
  ) {
    this.initStorage();
  }

  async initStorage() {
    await this.storage.create();
  }

  startApp() {
    this.router
      .navigateByUrl('/home', { replaceUrl: true })
      .then(() => this.storage.set('ion_did_tutorial', true));
  }

  onSlideChangeStart(event) {
    event.target.isEnd().then(isEnd => {
      this.showSkip = !isEnd;
    });
  }

  ionViewWillEnter() {
    this.storage.get('ion_did_tutorial').then(res => {
      if (res === true) {
        this.router.navigateByUrl('/home', { replaceUrl: true });
      }
    });

    this.menu.enable(false);
  }

  ionViewDidLeave() {
    this.menu.enable(true);
  }
}
<ion-header no-border>
  <ion-toolbar>
    <ion-buttons slot="end">
      <ion-button color='primary' (click)="startApp()" [hidden]="!showSkip">Skip</ion-button>
    </ion-buttons>
  </ion-toolbar>
</ion-header>

<ion-content fullscreen="true">
  <ion-slides #slides (ionSlideWillChange)="onSlideChangeStart($event)" pager="true">
    <ion-slide>
      <img src="assets/img/black-beauty.png" class="slide-image" />
      <h2 class="slide-title">
        <b><ion-text>SAFE</ion-text> <ion-text color="primary">SPACE</ion-text></b>
      </h2>
      <p>
        NSeye-Corp &reg; in colaboration with Salt & Pepper &reg; .
        <ion-text color="primary"><b> Gender-based violence (GBV)</b></ion-text> platform to raise awareness on GBV.
      </p>
    </ion-slide>

    <ion-slide>
      <img src="assets/img/broken-mirror.png" class="slide-image" />
      <h2 class="slide-title">What is Gender-Based Violence (GBV)?</h2>
      <p>
        <b>Gender-Based Violence</b> is 'any act of gender-based violence that results in, 
        or is likely to result in, physical, sexual or psychological harm or suffering to women.</p>
    </ion-slide>

    <ion-slide>
      <img src="assets/img/alone-at-beach.png" class="slide-image" />
      <h2 class="slide-title">There is no Boundary !!!</h2>
      <p>
        <b>One</b> characteristic of gender-based violence is that it knows no social or 
        economic boundaries and affects women and girls of all socio-economic backgrounds.</p>
    </ion-slide>

    <ion-slide>
      <img src="assets/img/women.png" class="slide-image" />
      <h2 class="slide-title">SAFE <ion-icon color="primary" name="home"></ion-icon> SPACE</h2>
      <ion-button fill="clear" (click)="startApp()">
        Welcome
        <ion-icon slot="end" name="arrow-forward"></ion-icon>
      </ion-button>
    </ion-slide>
  </ion-slides>
</ion-content>
