import { Component, ViewChild } from '@angular/core';
import { IonSlides, IonicModule } from '@ionic/angular';

import { CommonModule } from '@angular/common';
import { MenuController } from '@ionic/angular';
import { Router } from '@angular/router';
import { Storage } from '@ionic/storage-angular';

@Component({
  selector: 'page-welcome',
  templateUrl: './welcome.html',
  styleUrls: ['./welcome.scss'],
  standalone: true,
  imports: [
    CommonModule,
    IonicModule
  ]
})
export class WelcomePage {
  showSkip = true;

  @ViewChild('slides') slides: IonSlides;

  constructor(
    public menu: MenuController,
    public router: Router,
    public storage: Storage
  ) {
    this.initStorage();
  }

  async initStorage() {
    await this.storage.create();
  }

  startApp() {
    this.router
      .navigateByUrl('/home', { replaceUrl: true })
      .then(() => this.storage.set('ion_did_tutorial', true));
  }

  async onSlideChange() {
    const isEnd = await this.slides.isEnd();
    this.showSkip = !isEnd;
  }

  ionViewWillEnter() {
    this.storage.get('ion_did_tutorial').then(res => {
      if (res === true) {
        this.router.navigateByUrl('/home', { replaceUrl: true });
      }
    });

    this.menu.enable(false);
  }

  ionViewDidLeave() {
    this.menu.enable(true);
  }
}
<ion-header [translucent]="true">
  <ion-toolbar>
    <ion-buttons slot="end">
      <ion-button color="primary" (click)="startApp()" [hidden]="!showSkip">Skip</ion-button>
    </ion-buttons>
  </ion-toolbar>
</ion-header>

<ion-content [fullscreen]="true">
  <swiper-container [config]="config" (swiperslidechange)="onSlideChange($event)">
    <swiper-slide>
      <div class="slide-container">
        <img src="assets/img/ica-slidebox-img-1.png" class="slide-image" />
        <h2 class="slide-title">
          Welcome to <b>SAFE SPACE</b>
        </h2>
        <p>
          A <b>Gender-based violence (GBV)</b> platform to raise awareness on GBV.
        </p>
      </div>
    </swiper-slide>

    <swiper-slide>
      <div class="slide-container">
        <img src="assets/img/ica-slidebox-img-2.png" class="slide-image" />
        <h2 class="slide-title">What is GBV?</h2>
        <p>
          <b>Gender-based violence</b> is violence directed against a person because of their gender.
        </p>
      </div>
    </swiper-slide>

    <swiper-slide>
      <div class="slide-container">
        <img src="assets/img/ica-slidebox-img-3.png" class="slide-image" />
        <h2 class="slide-title">How can we help?</h2>
        <p>
          We provide resources, support, and a safe space to discuss and address GBV issues.
        </p>
      </div>
    </swiper-slide>

    <swiper-slide>
      <div class="slide-container">
        <img src="assets/img/ica-slidebox-img-4.png" class="slide-image" />
        <h2 class="slide-title">Ready to Start?</h2>
        <ion-button fill="clear" (click)="startApp()">
          Continue
          <ion-icon slot="end" name="arrow-forward"></ion-icon>
        </ion-button>
      </div>
    </swiper-slide>
  </swiper-container>
</ion-content>
